void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 nuv = uv * 2.0 - 1.0;

    nuv = nuv * vec2(iResolution.x / iResolution.y, 1.0);

    float t = iTime;
    float bpm = 1.0 / 60.0 * 65.0;
    float bt = t * bpm;
    float pi = 3.14159265359;
    float btp = bt * pi;
    float btp34 = btp - bpm * 0.5;

    nuv = vec2(
        tan(nuv.x * 1.4 + btp * 0.1),
        nuv.y
    );

    float f0 = pow(abs(sin(btp34)), 4.0);
    float m0 = texture(iChannel0, vec2(0.0, 0.25)).x;
    float m1 = texture(iChannel0, vec2(0.9, 0.25)).x;

    m0 = pow(m0, 1.9) * 2.5;
    m1 = pow(m1, 2.0);

    vec2 tu0 = nuv + vec2(uv.y + bt * 1.2, bt * 1.2);
    float t0 = texture(iChannel1, tu0 * 1.0 - nuv.x * 1.2).x;
    float t1 = texture(iChannel1, tu0 * 0.2 + vec2(0.0, bt * -0.1)).x;

    float f1 = pow(t0, 8.0) + sin(nuv.x * 20.0 + uv.y * -20.0 + bt * 12.0) * 0.7;
    float f2 = sin(uv.y * -32.0 + bt * 0.4 * sin(nuv.x * 2.0)) * 0.3;

    f2 += m0 * 0.5;

    float ff = pow(1.0 - length(nuv * vec2(0.5, 1.2)), 1.2);

    vec3 c = vec3(-0.1);

    c.x += 0.0;
    c.y += max(0.0, f1 * (f2 + pow(abs(nuv.y), sin(1.0 + btp34))) * ff);
    c.z += max(0.0, t1 * ff * f0) * pow((1.0 - abs(nuv.y)), 11.0) * 1.0 + 0.3 * pow((1.0 - abs(nuv.x)), 3.0) * m0;
    c.z += pow((1.0 - abs(nuv.x)), 120.0) * 4.0 * ff;
    c.z += c.y * ff * m1 * f0 * 4.0;
    c.x = c.z * m1 + c.z * (f0 * 0.1);

    //c.xyz += 0.1 * ff;

    fragColor.xyz = c;
}
