mat2 rotmat() {
    float a = iTime;
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float dist(vec3 p) {
    mat2 r = rotmat();
    p.xy *= r;
    p.yz *= r;
    p.xz *= r;
    vec3 delta = abs(p) - 0.6;
    return length(max(delta, 0.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = 2.0 * (fragCoord.xy/iResolution.xy - 0.5);
    uv.x *= iResolution.x/iResolution.y;

    vec3 d = normalize(vec3(uv, -1.0));
    vec3 p = vec3(uv, 1.0);
    vec3 o = p;
    int i=0;
    float dst;
    for(i=0; i<60; i++) {
        dst = dist(p);
        if(dst < 0.01) {
            break;
        }
        p += dst*d;
    }
    if(dst < 0.1) {
        fragColor = vec4(length(p-o)*0.5);
    } else {
        fragColor = vec4(uv, 0.0, 0.0);
    }
}
