void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 green  = vec3(0.0, 111.0 / 255.0, 62.0 / 255.0);
    vec3 yellow = vec3(254.0 / 255.0, 236.0 / 255.0, 28.0 / 255.0);
    vec3 red    = vec3 (254.0 / 255.0, 15.0 / 255.0, 0.0);

    float delay = 2.5;

    if (iTime < delay)
   		fragColor = vec4(mix(green, yellow, iTime / delay), 1.0);

    else
   		fragColor = vec4(mix(yellow, red, (iTime - delay) / delay), 1.0 - (sin(iTime) / 3.0));
}
