float circle (vec2 uv, vec2 pos, float rad, float blur) {

    float dist = length(uv-pos);    
    float circle = smoothstep(rad, rad-blur, dist);

    //if (dist < 0.2) circle = 0.4; //else circle = 0.0;

    return circle;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv - 0.5;
    uv.x = uv.x * iResolution.x / iResolution.y;

    float x = 0.2;

    float c0 = circle(uv, vec2(0.0, 0.0), 0.1, 0.01);
    float c1 = circle(uv, vec2(sin(iTime)*0.3, 0.0), 0.1, 0.01);
    float c2 = circle(uv, vec2(0.0, sin(iTime)*0.3), 0.1, 0.01);
    float c3 = circle(uv, vec2(sin(iTime)*-0.3, 0.0), 0.1, 0.01);
    float c4 = circle(uv, vec2(0.0, sin(iTime)*-0.3), 0.1, 0.01);

    float c5 = circle(uv, vec2(0.0, 0.0), 0.1, 0.01);
    float c6 = circle(uv, vec2(sin(iTime)*-x, 0.0), 0.1, 0.01);
    float c7 = circle(uv, vec2(0.0, sin(iTime)*-x), 0.1, 0.01);
    float c8 = circle(uv, vec2(sin(iTime)*x, 0.0), 0.1, 0.01);
    float c9 = circle(uv, vec2(0.0, sin(iTime)*x), 0.1, 0.01);

    vec3 c = vec3 (0.0, 1.0, 0.0)*c5;
    vec3 col = vec3(0.0, 1.0, 0.0)*((c0+c1+c2+c3+c4)-(c6+c7+c8+c9));

    fragColor = vec4(col+c, 1.0);
}
