float stripe(float interval,float uorv){
    float a=interval;
    float s=(uorv-floor(uorv/a)*a)/a;
    if(s<=1./4.||s>=3./4.){
       return 0.0;
    }else{
        return 1.0;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float interval=0.08;
    vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(stripe(interval,uv.x)*stripe(interval,uv.y)*(uv.y)*(1.-uv.x)*1.3,0,(1.-stripe(interval,uv.x)*stripe(interval,uv.y))*(1.-uv.y)*(uv.x)*0.3,1.0);
    uv=-1.0+2.*uv;
    for(int i=0;i<10;i++){
        float y= uv.y+sin(uv.x*3.+iTime+float(i)*0.2)*0.5;
        float ramx=0.05;
        float r= abs(y)-ramx;
        if(r<=0.){
            fragColor=vec4(1.9,1.,1.5,1.)*(r)/-ramx;
        }
    }
}
