renderpass.code:
float noise(vec2 p){
    return texture(iChannel0,p).r;
}
void mainImage( out vec4 o, in vec2 p )
{
    vec2 R = iResolution.xy;
	vec2 uv = (p.xy*2.-R) / min(R.x,R.y);
    vec2 uv2= uv*mix(1.2,1.5,(sin(iTime*200.)/2. +.5));
    o = texture(iChannel1,uv*.5+1.)*vec4(1.-min(.85,length(uv2)*mix(0.9,1.1,noise(uv))));
}
