void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	vec2 uv = fragCoord.xy / iResolution.xy;
    float tm=iTime/2.;
    float freq=texture(iChannel0,uv).x*2.0;
    vec3  tc=texture(iChannel1,uv).rgb;

    vec2 pos = uv;

	vec3 color = vec3(freq-sin(tm), pos.x, -pos.y+freq);


	color *= abs(1.0 / (sin(-0.7+pos.y + cos(pos.y ) * 0.2) * 10.0));
    color *= abs(1.0 / (sin(-0.7+pos.x + cos(pos.x ) * 0.2) * 10.0+cos(tm)));




    fragColor = vec4(-1.0+color*freq+tc, 1.0 );

}
