vec2 fakemouseinput( float t ) {

    float a = sin( t );
    float b = 8.0;
    float x = a * ( cos( t ) * b - cos( b * t ) );
    float y = a * ( sin( t ) * b - sin( b * t ) );
    return vec2( x, y ) * 0.5;
}

float tinkeredsmoothstep( vec2 uv, float a, float b ) {
    float c = pow( abs(uv.x), uv.x * a + b );
    return (1.0 - smoothstep( c * uv.y * uv.x, c, uv.y ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mp = iMouse.xy / iResolution.xy - 0.5;

    if ( 0. >= (iMouse.z+iMouse.w) ) {
        mp = fakemouseinput( iTime * 0.25 );
    }

    float a = clamp( mp.x * 30.0, -15.0, 15.0 );
    float b = clamp( mp.y * 30.0, 0.0, 15.0 );

    vec4 one = vec4( 1.0, 1.0, 1.0, 1.0 ); 

    fragColor =
        one * tinkeredsmoothstep( uv, a, b ) - 
        one * tinkeredsmoothstep( 1. - uv, -a, b )
        ;

}
