#define C(r)   t = max(t, r - max(max(a.x,a.y),a.z)  )
#define R(x,y) t = max(t, max(abs(q.x+q.y),abs(q.x-q.y)) /1.41 )  

void mainImage(out vec4 O, vec2 U) {

    float t = iTime;
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),    // ray direction
          p = 30./q, a;                       // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p,
        q.xz *= R, q.yz *= R,                 // rotation
        t-=t,
        R(x,y), R(y,z), R(x,z) -6.,           // octaedron
        a = abs(mod(q,10.)-5.), C(4.9),       // 8 corner holes
        a = abs(q),             C(4.),        // inner cube hole
        p += t*D;                             // step forward = dist to obj
}
