// Fork of "Decor Wall Tiles" by markusm. https://shadertoy.com/view/4dyyDh
// shapes groups version: https://www.shadertoy.com/view/4stBz4

const float CELL =  14.,        // grid size vertically -> cell size
           RATIO = 2.8,         // brick length / brick width
          JITTER = 3.;          // jitter Voronoi amount. 1 = stay in cell
vec2  CYCLE = vec2(5,10);       // pattern repeat scale ( in #brick )

// --- 1D version Voronoi. IQ version https://www.shadertoy.com/view/MslGD8

float hash( vec2 p ) { return fract(sin(dot(p,vec2(127.1,311.7)))*18.5453); }

#define disp(p) ( -(JITTER-1.)/2. + JITTER * (p) )

vec2 voronoi( vec2 u )
{
    vec2  n = floor( u );
    float f = fract( u.x );

	vec2 m = vec2(1e5);
    for( float g=-2.; g<=2.; g++ )
        {
            float o = hash(mod(n + vec2(g,0),CYCLE)),
                  d = abs(g - f + disp(o));
            if( d < m.x )
                m = vec2( d, o );
        }

    return m;
}

// --- Returns the stone tile height and id at the given pos

vec2 stone( vec2 uv )
{
    uv *= CELL; uv.x /= RATIO;
    uv.y = floor( uv.y  );
    vec2 c = voronoi( uv );
    float height = c.y * .7;

    return vec2( height, c.y );
}

// --

void mainImage( out vec4 O, vec2 U )
{
    vec2 p = U/iResolution.y;

    p.y += iTime * 0.2;

    vec2 res = stone( p );

    O = vec4(res.y);
}
