bool vrStencilCull(vec2 uv)
{
    uv = uv - vec2(0.5);
    float centerLength = -0.5+length(vec2(-0.02, 0.0)+uv * vec2(0.89, 0.97));
    float sideSdf = -0.5 + length(uv*vec2(1.0, 1.1) + vec2(-0.9, 0.0));
    return max(centerLength, -sideSdf) < 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;   
    fragColor = vrStencilCull(uv) ? vec4(1.0) : vec4(0.0);
}
