#define PI 3.14159

float circle(vec2 uv, vec2 pos, float radius, float blur)
{
    return 1.0 - smoothstep(radius, radius + blur + 0.001, length(uv - pos));
}

float defaultBlur = 0.01;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.y /= iResolution.x / iResolution.y;

    // Time varying pixel color
    vec3 psych = 0.5 + 0.5*cos(iTime+(fragCoord/iResolution.xy).xy.xyx+vec3(0.01,2,4));

    float xspeed = 0.5;
    float yspeed = 0.1;

    float circles = circle(uv, vec2(sin(iTime * xspeed)/32.0 + 0.0, 		cos(iTime * yspeed)/3.0), 0.2, defaultBlur);
    circles += 		circle(uv, vec2(sin(iTime * xspeed)/6.0 + -0.3, 	cos(iTime * yspeed)/4.0), 0.1, defaultBlur*2.0);
    circles += 		circle(uv, vec2(sin(iTime * xspeed)/6.0 + 0.3, 		cos(iTime * yspeed)/4.0), 0.1, defaultBlur*2.0);

    circles += 		circle(uv, vec2(sin(iTime * xspeed)/4.0 + -0.45, 	cos(iTime * yspeed)/5.0), 0.05, defaultBlur*4.0);
    circles += 		circle(uv, vec2(sin(iTime * xspeed)/4.0 + 0.45, 	cos(iTime * yspeed)/5.0), 0.05, defaultBlur*4.0);


    // using circle as a mask
    //vec3 mask = mix(vec3(0.0), vec3( 1.0),);



    // Output to screen
    fragColor = mix(vec4(1.0), vec4(psych, 1.0), min(circles, 1.0));
}
