renderpass.code:
float xor(vec2 p)
{
    float f = 0.;
    for (int i = 0; i < 8; ++i)
    {
        p = 2.0 * fract(p);
        f *= 2.;
        f += mod(floor(p.x) + floor(p.y), 2.);
    }
    return f / 256.;
}

vec4 makeSpiral(vec2 uv, float v, float d, vec4 c1, vec4 c2)
{
    float f = 1. / length(uv);
    f += atan(uv.x, uv.y) / acos(0.) * d;
    f += v * iTime;
    f = fract(f) * 2. - 1.;

    vec4 c = vec4(0.);

    if (f >= 0.0)
    {
        c = mix(c1, c2, min(f, 1.));
        c.rgb *= sin(length(uv) - .2);
    }

    return c;
}

vec4 makeXorTube(vec2 uv)
{
    float f = 1. / length(uv) + iTime;
    float s = atan(uv.x, uv.y) /  acos(0.);
    f = xor(vec2(f, s)) * (length(uv) - .2);
    f = clamp(f, 0., 1.);
	return vec4(f*.7, f*.9, f, 1.0);
}

vec4 blend(vec4 c1, vec4 c2)
{
    return vec4(mix(c1.rgb, c2.rgb, c2.a), max(c1.a, c2.a)); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy / .5 - 1.;
    uv.x *= iResolution.x / iResolution.y;

    vec4 c0 = makeXorTube(uv*0.7);
    vec4 c1 = makeSpiral(uv*1.4, 1.,  1., vec4(.8, .4, .4, 1.), vec4(1., .8, .4, 0.8));
    vec4 c2 = makeSpiral(uv,     1., -1., vec4(.4, .6, .4, 1.), vec4(.6, .8, .4, 0.8));

    fragColor = blend(c0, blend(c2, c1));
}
