// Source:
// http://http.developer.nvidia.com/GPUGems/gpugems_ch22.html

float single_channel_level_filter(in float channel)
{
    // These mimic what you see in Photoshop level editor
    // Range is between 0 and 255 just like Photoshop.

    const float INPUT_BLACK = 74.00;
    const float INPUT_GAMMA = 1.000;
    const float INPUT_WHITE = 183.0;
    const float OUTPUT_BLACK = 0.000;
    const float OUTPUT_WHITE = 255.0;

    return (pow(((channel * 255.0) - INPUT_BLACK) / (INPUT_WHITE - INPUT_BLACK),
           INPUT_GAMMA) * (OUTPUT_WHITE - OUTPUT_BLACK) + OUTPUT_BLACK) / 255.0;
}

void multi_channel_level_filter(inout vec4 color)
{
    color.r = single_channel_level_filter(color.r);
    color.g = single_channel_level_filter(color.g);
    color.b = single_channel_level_filter(color.b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
    multi_channel_level_filter(fragColor);
}
