void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float dotsize = 5.0 * (2.0 + sin (0.1 * iTime));
    vec2 mirrorCoord = vec2 (iResolution.x - fragCoord.x, fragCoord.y);
    vec2 dotcenter = (mirrorCoord - mod(mirrorCoord, 2.0 * dotsize)) + vec2(dotsize);
    float rad = length(mirrorCoord - dotcenter);

    vec2 sampleAt = dotcenter / iResolution.xy;

    vec2 musicSampleAt = vec2(fract(dot(sampleAt, vec2(311.7123, 733.01))), 0.0);

    float dotRad = texture(iChannel1, musicSampleAt).x * (dotsize - 2.0);

    float inside = smoothstep (2.0, 0.0, rad - dotRad);

    vec3 rgb = texture(iChannel0, sampleAt).xyz;

    rgb = 0.25 * (step(vec3(0.1), rgb) + step(vec3(0.25), rgb) +
                  step(vec3(0.4), rgb) + step(vec3(0.55), rgb));
   //  rgb = vec3(1.0, 0.0, 0.0);

    vec3 background = vec3(0.0);
	fragColor = vec4(mix(background, rgb, inside),1.0);
}
