//Code optimization and initialization fix by FabriceNeyret2
void mainImage(out vec4 o, vec2 i)
{
    vec3 p,q;
    float d, a=0.;

    for (int l = 0; l < 64; l++) {
        vec2 v = sin(a*.1 + vec2(0,1.6));
        q = normalize(vec3((i/iResolution.xy-.5)*mat2(v,-v.y,v.x),1));
        p = iDate.w + q*a;
        d = max(0., length( cos(p-cos(p*3.3)) + cos(p*.1)*1.4 ) - .4);
        a += d*.5;
    }
    o.xyz = d*cos(q)*.65;
}

