#define cx_mul(a, b) vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x)
#define cx_div(a, b) vec2(((a.x*b.x+a.y*b.y)/(b.x*b.x+b.y*b.y)),((a.y*b.x-a.x*b.y)/(b.x*b.x+b.y*b.y)))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(0.5,0.5);

    vec2 val = uv*(15.0+iTime)/100.0;
    if (val.x > 5.0)
        val = val - iTime/5.0;
    for (int i=0; i < 20; i++) {
        vec2 val2 = cx_mul(val,val);
        vec2 val3 = cx_mul(val2,val);
        vec2 val4 = cx_mul(val2,val2);
        val = val - cx_div((val4 - vec2(1,0)),(4.0*val3));        
    }
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec4 base_color;
    float diff;
    float tolerance = 0.1+sin(iTime)/10.0;
    if (abs(val.x-1.0)<tolerance){
        base_color.r = 1.0;
        diff = abs(val.x-1.0);
    }
    else if (abs(val.y-1.0)<tolerance) {
        base_color.g = 1.0;
        diff = abs(val.y-1.0);
    }
    else if (abs(val.x+1.0)<tolerance) {
        base_color.b = 1.0;
        diff = abs(val.x+1.0);
    }
    else if (abs(val.y+1.0)<tolerance) {        
        base_color.g = 1.0;
        base_color.b = 1.0;
        diff = abs(val.y+1.0);
    }
    //base_color = base_color * diff*100.0;
    if (diff > 0.000001)
        base_color = base_color*0.8;
    if (diff > 0.01)
        base_color = base_color*0.8;
    if (diff > 0.05)
        base_color = base_color*0.8;
    if (diff > 0.2)
        base_color = base_color*0.8;
    fragColor = base_color;//vec4(val.x,val.x,val.y,1.0);
}
