float dots(vec2 st) {
    vec2 st2 = st * 9.728;
    float radius = 0.3 + sin(iTime * 3.0) * 0.056 + st.x * 0.220;
    return smoothstep(radius, radius+0.05, distance(st2, vec2(floor(st2.x), floor(st2.y)) + vec2(0.5, 0.5)));
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float rotated_dots(vec2 st) {
    return dots((st + vec2(-0.5)) * rotate2d(sin(iTime*0.3)*3.0));
}

float circle(vec2 st, float boundary) {
    float border = 0.004;
    return smoothstep(boundary-border, boundary+border, distance(vec2(0.5, 0.5), st));
}

float pulse(float x) {
    const float k = 6.384;
    float h = k*x;
    return h*exp(1.0-h);
}

float pulses() {
    const float speed = 1.872;
    return pulse(fract(iTime * speed))*0.05 + 0.3;
}

float waves(vec2 st) {
    st *= rotate2d(iTime);
    vec2 st2 = vec2(sin(st.x * 4.608), sin(st.y * 2.336));
    st2 *= rotate2d(iTime);
    float patt = sin(st2.x * 30.936 + 5.932) + sin(st2.x * 24.0 - 10.376) + sin(st2.y * 29.0 + 2.372) + sin(st2.y * 45.0);
    return smoothstep(0.3, 0.6, patt);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    st += vec2(-iResolution.y/(1.5*iResolution.x), 0.);

    vec3 background = mix(vec3(0.043,0.800,0.599), vec3(0.053,0.980,0.734), rotated_dots(st));
    vec3 fgcircle = mix(vec3(1.000,0.070,0.464), vec3(0.620,0.022,0.429), waves(st));
    vec3 foreground = mix(fgcircle, background, vec3(circle(st, pulses())));

    fragColor = vec4(foreground, 1.0);
}
