//#define INT_MODE //uncomment this for integer mode

const vec2 SPEED = vec2(.001, .0001); //(SPEEDX, SPEEDY) //try *10 or *100 speed at INT_MODE
const vec2 ZERO = vec2(0, 0);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
    vec2 mousexy = iMouse.xy;
    if (mousexy.xy==ZERO) mousexy = vec2(100.0, 1.0); //aesthetic workarround for total blackness at (0, 0)

    vec2 p = mousexy * (fragCoord/iResolution.xy + iTime*SPEED);

#ifdef INT_MODE
    int c = int(p.x) % int(p.y);
#else
    float c = mod(p.x, p.y);
#endif

    fragColor = vec4(c);
}
