void mainImage(out vec4 O,vec2 U) {
    O = texture(iChannel0, U/iResolution.xy);
    O = 3.*O + .4*O.a;
}

void mainImage(out vec4 O,vec2 U) {
    //float t = 0.;
    //float t = 1.57;
    float t = .3*iTime;

    vec2 R = iResolution.xy;
    O = 2.* texture(iChannel2, (U+R.y*fract(1e4*sin(1e3*iTime*vec2(1,17.31))))/256.) -1.;
	O += .05;
    U /= R;
    vec4 G = 2.*texture(iChannel1, 8./16. + U/4.,0.)-1.;
    //G = normalize(G);
    U += .001* G.gb * mat2(cos(t+vec4(0,33,11,0)));
    O = .1*O + .95*texture(iChannel0, U);
    O.a  = .5+.5*G.r;
}
