void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ft;

    // Generate co-ordinates between -1 and +1
	vec2 uv = (fragCoord.xy / iResolution.xy)*2.0-1.0;
    // Correct for aspect ratio
	uv.x *= iResolution.x / iResolution.y;

    // Move to the first ring position
	uv.y -= 0.25;   
    uv.x += 1.0;

    // Blue ring
    ft = smoothstep(0.3, 0.4, length(uv)) - smoothstep(0.4, 0.5, length(uv));

 	fragColor = vec4(0.0, 0.0, ft, 1.0);

    uv.x -= 1.0;

    // White ring
    ft = smoothstep(0.3, 0.4, length(uv)) - smoothstep(0.4, 0.5, length(uv));

 	fragColor = max(vec4(ft, ft, ft, 1.0), fragColor);

    uv.x -= 1.0;

    // Red ring
    ft = smoothstep(0.3, 0.4, length(uv)) - smoothstep(0.4, 0.5, length(uv));

 	fragColor = max(vec4(ft, 0.0, 0.0, 1.0), fragColor);

    uv.y += 0.45;
    uv.x += 1.5;

    // Yellow ring
    ft = smoothstep(0.3, 0.4, length(uv)) - smoothstep(0.4, 0.5, length(uv));

 	fragColor = max(vec4(ft, ft, 0.0, 1.0), fragColor);

    uv.x -= 1.0;

    // Green ring
    ft = smoothstep(0.3, 0.4, length(uv)) - smoothstep(0.4, 0.5, length(uv));

 	fragColor = max(vec4(0.0, ft, 0.0, 1.0), fragColor);
}
