#define AA 4.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragColor = vec4(0.0);
    for(float aaX=.0; aaX < AA; aaX++)
    {
    	for(float aaY=.0; aaY < AA; aaY++)
        {
            vec2 uv = (fragCoord.xy + vec2(aaX, aaY)/AA) / iResolution.y;
            uv = uv - 0.5*vec2(iResolution.x/iResolution.y, 1.0);

            float N0 = 7.0;
            float N1 = 5.0;

            float r = length(uv);
            float theta = atan(uv.y, uv.x);

            float t0 = log(r) - theta + 0.25*iTime;
            t0 = t0 / 2.0 / 3.14 + 0.5;
            t0 = N0*mod(t0, 1.0/N0);

            float t1 = log(r) + theta + 1.0*iTime;
            t1 = t1 / 2.0 / 3.14 + 0.5;
            t1 = N1*mod(t1, 1.0/N1);  

            float t=0.0;
            if(t0 > 0.5)
                t = 1.0;
            if(t1 > 0.5)
                t = 1.0-t;

			fragColor += vec4(vec3(t),1.0);
        }
    }

	fragColor = fragColor / AA / AA;
}
