float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
void baseLayer (out vec4 layer1Output, in vec2 uv)
{
   float c = sin(uv.y*3.1415*2.0*3.0*1.0) * 0.5 + 0.8 + 0.5;
   layer1Output = vec4(c,c,c,1.0);
}    
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 outPutLayer1 = vec4(0.0,0.0,0.0,0.0);
    vec4 outPutLayer2 = vec4(0.0,0.0,0.0,0.0);
    vec4 outPutLayer3 = vec4(0.0,0.0,0.0,0.0);
    vec2 uv = fragCoord.xy / iResolution.xy;
    baseLayer(outPutLayer1, uv);


	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);



    uv.x *= 2.0;
    uv.y *= 2.0;

    vec4 distortionColor = texture(iChannel1, uv);
    vec4 distortionColor2 = texture(iChannel2, uv);

    distortionColor2.x = iTime * 2.0 * rand(vec2(0.0,1.0));
    distortionColor2.y = iTime * 8.0;

    uv.x += distortionColor.x / 15.0 + iTime / 5.0;
	uv.y += distortionColor.y / 10.0 + iTime / 5.0;

    uv.x += -distortionColor2.x / 25.0 + iTime / 10.0;
	uv.y += -distortionColor2.y / 25.0 + iTime / 10.0;

    fragColor = outPutLayer1 * texture(iChannel1, uv);

    fragColor = fragColor * vec4(0.5,0.3,0.2,1.0);

}
