#define PI 3.141592

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



    vec2 uv = fragCoord.xy / iResolution.xy;

	vec4 color = vec4(uv,.5+.5*sin(iTime),1.);

    vec4 color2 = vec4(1. - ((uv.x + uv.y) / 2.),uv,1.);

    vec2 pos = uv*2.-1.;


    color = color2 * abs(1./(sin(pos.y + cos(pos.x*.5 + iTime)*.8)*10.));

    fragColor = color;
}
