void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	ivec2 p = ivec2( fragCoord-0.5);
    ivec2 m = ivec2( iResolution.xy/2.0 );

    float f = (((p.x+p.y)&1)==1) ? 1.0 : 0.0;
    f *= (p.x<3*m.x/4) ? 1.0 : 0.0;
    f += (p.x==m.x && p.y==m.y) ? 1.0 : 0.0;

	fragColor = vec4( f, f, f, 1.0 );
}
