// Circular Timer - 2015 by JT
// A simple circular timer visualization for use as UI-element (e.g. in computer-games).

// Minified by FabriceNeyret2
void mainImage( out vec4 C, vec2 I )
{
    vec2 R = iResolution.xy;
    I+= I-R;
    float a = fract( atan(I.y, I.x)/6.28 );

    C += vec4(0,a,1.-a,1) * smoothstep(.1, .09,abs(length(I)/R.y-.9)) 
                          * smoothstep(a,a+5e-3, fract(iDate.w*.1));
}
