#define R iResolution
#define T iTime
#define EPS .02

//#define COLOR

float wings(in vec2 p, in vec2 ap)
{
    return abs(pow(p.x/7.,2.)*sqrt(sign(ap.x-3.))+pow(p.y/3.,2.)*sqrt(sign(p.y+(3.*sqrt(33.))/7.))-1.)*4.;
}

float tail(in vec2 p, in vec2 ap)
{
    return abs(abs(p.x/2.)-(((3.*sqrt(33.))-7.)/112.)*pow(p.x,2.)-3.+sqrt(1.-pow((abs(ap.x-2.)-1.),2.))-p.y);
}

float neck(in vec2 p, in vec2 ap)
{
    return abs(-(9.*sqrt(sign((1.-ap.x)*(ap.x-.75)))-8.*ap.x-p.y))/4.;
}

float hears(in vec2 p, in vec2 ap)
{
    return abs(3.*ap.x+.75*sqrt(sign((.75-ap.x)*(ap.x-.5)))-p.y)/2.;
}

float front(in vec2 p, in vec2 ap)
{
    return abs(2.25*sqrt(sign((.5-p.x)*(p.x+.5)))-p.y);
}

float shoulders(in vec2 p, in vec2 ap)
{
    return abs(6.*sqrt(10.)/7.+(1.5-.5*ap.x)*sqrt(sign(ap.x-1.))-((6.*sqrt(10.)/14.)*sqrt(4.-pow(ap.x-1.,2.))+p.y));
}

vec2 batman(in vec2 p)
{
    vec2 ap = abs(p);
    if (wings(p, ap) < EPS)     // part red
        return vec2(1, 0);
    if (tail(p, ap) < EPS)      // part green
        return vec2(1, 1);
    if (neck(p, ap) < EPS)      // part blue
        return vec2(1, 2);
    if (hears(p, ap) < EPS)     // part magenta
        return vec2(1, 3);
    if (front(p, ap) < EPS)     // part cyan
        return vec2(1, 4);
    if (shoulders(p, ap) < EPS) // part yellow
        return vec2(1, 5);
	return vec2(0, 0);
}


void mainImage(out vec4 C, in vec2 U)
{
	vec2 uv = 2. * U.xy / R.xy - 1.;
    uv.x *= R.x / R.y;
	C = vec4(vec3(0), 1);
    vec2 bat = batman(uv*vec2(5, 4));
    if (bat.x == 1.)
    {
        C.rgb = vec3(1);
        #ifdef COLOR
        if (bat.y < 1.)
            C.rgb = vec3(1, 0, 0);
        else if (bat.y < 2.)
            C.rgb = vec3(0, 1, 0);
        else if (bat.y < 3.)
            C.rgb = vec3(0, 0, 1);
        else if (bat.y < 4.)
            C.rgb = vec3(1, 0, 1);
        else if (bat.y < 5.)
            C.rgb = vec3(0, 1, 1);
        else
            C.rgb = vec3(1, 1, 0);
        #endif
    }
}
