vec3 a = vec3(2.0,52.0,56.0)/255.0;
vec3 b = vec3(230.0,61.0,76.0)/255.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  fragCoord / iResolution.xy;
    vec2 tc = vec2(uv.xy);
    float t = mod(iTime,1.0)*2.0;
    tc.x += 0.5;
    vec3 n = texture( iChannel0,  tc).xyz;   

	float p = 0.75;
    float d = 0.2*n.b;
    tc.x+=sin(n.r*p)*d;
    tc.y+=cos(n.g*p)*d;
    vec3 m = texture( iChannel0,  tc).xyz;   

    if(uv.y>.5){
       if(m.b>0.4){
    discard;
    }else if(m.r > 0.4){
        m = b;
    }else{
       m = a;
    }
    }
   // m*= uv.x < 0.5 ? a:b;
    fragColor = vec4(m,1.0);
}
