void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = floor(64.0 * uv) / 64.0;

    vec3 green = vec3(0.0, 1.0, 0.0); // Couleur verte

    float w = texture(iChannel0, uv).x;

    // Calcul de la valeur de la couleur verte en fonction de la fr√©quence
    green *= 1.0 - smoothstep(0.0, clamp(0.0, 0.4, cos(w) * pow(w, 2.0)), abs(0.5 - uv.y));

    // Sortie de couleur verte seulement
    fragColor = vec4(green.g, 0.0, green.b, 1.0);
}



