#define PI 3.14159265359

float nsin (float x){
  return (sin (x) +1.)/2.;
}

float ncos (float x){
  return (cos (x) +1.)/2.;
}

float random (float x) { 
    return fract(sin(dot(vec2(x,.0),
                         vec2(12.9898,78.233)))* 
        43758.5453123);
}
float noise (float x){
  float i = floor(x);
  float f = fract(x);
  float y = random(i);
  float u = f * f * (3.0 - 2.0 * f );
  y = mix(random(i), random(i + 1.0), u);
  return y;
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Value Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float noisey2(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

vec3 mix_colors(float k){
  return mix( vec3(0.129, 0.180, 0.196), vec3(0.800, 0.529, 0.788), k);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);

    float N = 20.0;//u_mouse.x/u_resolution.x*40.;
    //float timer = fract(iTime/N);


    st = (st-vec2(.5))*2.0;

    float d = length(st);
    float a = atan(st.y,st.x);
    float nst = noisey2(st*10.04);

    //first pass
    float dfield = d * N + nsin(a)*20. + noisey2(vec2(d*3.0)+vec2(1000.0,1000.0))*10.0;
    float alternating = mod(dfield + iTime*2.0,2.0);//dfield = fract(dfield);
    alternating *= .01;
    alternating = mod(alternating,.2);
    float intensity =30.*alternating;
    vec3 color1 = mix_colors(intensity);

    //second pass
    float dfield2 = d * N + nsin(a)*60. + noisey2(vec2(d*3.0)+vec2(500.0,100.0))*10.0;
    float alternating2 = mod(dfield2 + iTime*4.0,6.0);
    // Add the shape on the foreground
    alternating2 *= .01;
    alternating2 = mod(alternating2,.2);
    float intensity2 =30.*alternating2;

    vec3 color2 = mix_colors(intensity2);

    color = min(color1, color2) + smoothstep(.9,1.0,color1);

    fragColor = vec4(color,1.0);
}
