#define RADIUS 5

vec4 MedianBlur(vec2 coord)
{
    float stepX = 1.0/iResolution.x;
    float stepY = 1.0/iResolution.y;

    float avgR = 0.0;
    float avgG = 0.0;
    float avgB = 0.0;
    float avgA = 0.0;
    int count = 0;

    for (int i = -RADIUS; i <= RADIUS; i++)
    {
        for (int j = -RADIUS; j <= RADIUS; j++)
        {
            float x = coord.x + stepX * float(i);
            float y = coord.y + stepY * float(j);
            if(x >= 0.0 && x <= 1.0 && y >= 0.0 && y <= 1.0)
            {
                vec2 currC = vec2(x, y);
                vec4 color = texture(iChannel0, currC);

                avgR += color.r;
                avgG += color.g;
                avgB += color.b;
                avgA += color.a;
                count++;
            }
        }
    }

    return vec4(avgR/float(count), avgG/float(count), avgB/float(count), avgA/float(count));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = MedianBlur(uv);
}
