void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 tex1Color = texture(iChannel0, fragCoord.xy / iResolution.xy);

    const int kSize = 5;
    vec3 avg = vec3(0.0);
    for (int i=-kSize; i <= kSize; ++i) {
        for (int j = -kSize; j <= kSize; ++j) {
            avg = avg + texture(iChannel0, (fragCoord.xy + vec2(float(i), float(j)))/iResolution.xy).xyz;
        }
    }
    int area = (2*kSize + 1) * (2*kSize + 1);
    avg = avg.xyz/vec3(area);

	fragColor = vec4(avg, tex1Color.a);
}
