void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = vec4(-uv.x*.1+0.3, uv.x*.1+uv.y *0.6 + 0.2 ,uv.y * 0.8 + 0.4,0);

    float factor  = 0.5 + sin(iTime * 0.31 + (uv.x * 1.0 * 3.1415)) * 0.1;
    float factor2 = 0.5 + sin(iTime * 0.32 + (uv.x * 1.4 * 3.1415)) * 0.11;

    float s = max(min(factor - uv.y, uv.y - factor2),min(uv.y-factor, factor2-uv.y));
	float d = smoothstep(-2.5*fwidth(uv.x),0.0,s);
	fragColor += d*vec4(uv.y*.9,uv.y*0.7,1,0);
}
