mat2 rotate = mat2( 0.8, 0.6, -0.6, 0.8 );

/**
 * hash thanks to Dave_Hoskins: https://www.shadertoy.com/view/4djSRW
 */
#define HASHSCALE1 .1031

float hash13(vec3 p3)
{
	p3  = fract(p3 * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

/* end of hash */



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 pos = uv;
    //pos += sin(iChannelTime[0] * 0.25);
    pos *= sin(iChannelTime[0] / 3.0);
    float angle = atan(uv.y-0.5, uv.x-0.5) * cos(iTime / 80.0) * 2.0;
    angle *= min(iChannelTime[0], 5000.0) / 5000.0;
    //pos += angle * cos(iChannelTime[0] / 5.0);
    rotate = mat2( cos(angle), -sin(angle), sin(angle), cos(angle) );
    pos *= rotate;
    pos += texture(iChannel0, pos.yx * 0.125).xz * 0.25;
    pos *= texture(iChannel1, pos * 0.125).xy + (iChannelTime[0]/500.0) * 0.025;
    pos += texture(iChannel1, pos.yx + uv * 0.125 + pos * 0.0125).xy + (iChannelTime[0]/500.0) * 0.025;

    pos -= 0.5;
    pos = vec2(cos(pos.x), sin(pos.y)) * 0.25;
    pos.x += cos(iTime * 0.125) * 0.125;
    angle = texture(iChannel0, vec2(0, 0)).z * 0.125;
    pos *= cos(pos.y) + (texture(iChannel0, pos).x * 0.25) - 0.5;
    rotate = mat2( cos(angle), -sin(angle), sin(angle), cos(angle) );
    pos *= rotate;
    pos *= 2.1;
    pos = clamp(pos, 0.0, 1.0);
    vec4 v = texture(iChannel0, pos) + (vec4(vec3(uv, 1.0), 1.0) * 0.125);
    pos *= pos;
    angle = texture(iChannel0, vec2(0.5, 0.5)).x * (iChannelTime[0] / 1000.0) * 3.141;
    rotate = mat2( cos(angle), -sin(angle), sin(angle), cos(angle) );
    v += texture(iChannel0, vec2(pos.y, pos.x));
    v += texture(iChannel1, rotate * uv + pos * 10.0);
    v /= 5.0;
    v *= v * 2.0;
    //v += (vec4(vec3(pos.y, pos.x * pos.y, pos.x), 1.0) * 0.7);
    v *= 1.1 * vec4(1.0, 1.0, 1.0, 1.0 - smoothstep(0.65, 1.1, abs(uv.x-0.5) * 2.0));
	v *= 1.1 * vec4(1.0, 1.0, 1.0, 1.0 - smoothstep(0.65, 1.1, abs(uv.y-0.5) * 2.0));    
    v += (hash13(vec3(rotate * uv * (sin(iTime / 10.0) * 100.0), iTime)) -0.5) * 0.1725;
    v = smoothstep(0.15, 0.5, v);
    vec4 background = vec4(0.0, 0.0, 0.0, 1.0);
	fragColor = background + (v * v.a); // * vec4(pos,0.5+0.5*sin(iTime),1.0);
}
