#define st( fun ) smoothstep( 0.99, 1.0, fun )

const vec4 red = vec4( 1.0, 0.0, 0.0, 1.0 );
const vec4 blue = vec4( 0.0, 0.0, 1.0, 1.0 );

#define fun(x) tan(100.0*x)/20.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord / iResolution.xy;
    p = 2.0 * p - 1.0;
    p *=  vec2( iResolution.x / iResolution.y, 1.0 );

	vec4 color = vec4( 1.0 );
    float timeFactor = sin( iTime );
    vec2 p2 = vec2( fun(p.x), fun(p.y) );
    float f = st( length( p ) + length(p2) * timeFactor );
	color = mix( red, color, f );

    fragColor = color;
}
