/*
Streaming Spectrogram v.2
Copyright 2017 Theron Tarigo
*/

float windowmax = 0.5;

vec4 encode(int i) { return vec4((uvec4(i)>>ivec4(0,8,16,24))&0xFFu)/255.; }
int decode(vec4 f) { uvec4 d = uvec4(f*255.)<<ivec4(0,8,16,24); return int(d.x|d.y|d.z|d.w); }

void mainImage( out vec4 f, in vec2 pixf )
{
    f = vec4(0.);
    ivec2 pix = ivec2(pixf);
    ivec2 res = ivec2(iChannelResolution[0].xy);
    int center = res.x*res.y/2;
    int offset = int(iTime*iSampleRate) - center;
    int index = res.x*pix.y+pix.x;
    if (index==0) { f = encode(offset); return; }
    int offsetlast = decode(texelFetch(iChannel0, ivec2(0), 0));
    int cutoff = int(windowmax*iSampleRate);
    if (abs(index-center) > cutoff) return;
    int samp = index+offset;
    int samplast = center+cutoff+offsetlast;
    float time = (float(samp)-0.5)/iSampleRate;

    //#define TESTING
    #ifdef TESTING
    /* TESTING: ensure reuse of previously computed samples is valid.  No blue pixels should appear! */
    vec2 newsamp = mainSound( in int samp,time);
    vec2 oldsamp = texelFetch(iChannel0, (ivec2(index+offset-offsetlast)/ivec2(1,res.x))%(res), 0).xy;
    f.xy = newsamp;
    if (bool(newsamp != oldsamp) == bool(samp <= offsetlast + center + cutoff)) f.z = 1.;
    #else
    if (samp <= offsetlast + center + cutoff) {
        f.xy = texelFetch(iChannel0, (ivec2(index+offset-offsetlast)/ivec2(1,res.x))%(res), 0).xy;
    } else {
        f.xy = mainSound(0,time);
    }
    #endif
}

// Config

const float timescale = 1./4.; // Screens per second
const float freqscale = 1000.;
const float aspect = 1.;
const vec2 stereomix = vec2(1.,1.);



/*
Streaming Spectrogram v.2
Copyright 2017 Theron Tarigo
*/

//const float pi = 3.141592653589793;

vec2 readsound( float time )
{
    ivec2 res = ivec2(iResolution.xy);
    int offset = int(iTime*iSampleRate) - res.x*res.y/2;
    //time += 4.;
    int samp = int(time*iSampleRate) - offset;
    ivec2 pix = ivec2(samp%res.x, (samp/res.x));
    return texelFetch(iChannel1, pix, 0).xy;
}

vec2 spectrogram(float windowmax, float time, float freq) {
    vec2 sum = vec2(0.);
    float window = windowmax;
    float samplerate = freqscale*2.;
    for (float trel = -window; trel <= window; trel+=1./samplerate) {
        // Cosine window
        float w = (0.5+0.5*cos(trel/window*pi))/samplerate/window;
        float samp = dot(stereomix,readsound(time+trel));
        sum += w*vec2(cos(trel*freq*2.*pi),sin(trel*freq*2.*pi))*samp;
    }
    return sum;
}

void panImage( out vec4 f, float time, float y ) {
    float freq = freqscale * y / iResolution.y;
    f.rgb = 5.*vec3(10.,3.2,1.)*
    length(spectrogram(0.1*sqrt(freq/freqscale)/aspect, time, freq));
}

//#define FRAMEDEBUGVIS
void mainImage( out vec4 f, in vec2 df )
{
    ivec2 d = ivec2(df);
    vec2 fsf = iResolution.xy;
    ivec2 fs = ivec2(fsf);
    int shift = int(fsf.x*timescale*(iTime));
    int shiftlast = int(fsf.x*timescale*(iTime-iTimeDelta));
    int delta = shift - shiftlast;
    if ((fs.x-d.x) <= delta) {
        #ifdef FRAMEDEBUGVIS
        if (d.y<10) { f = vec4(0,iFrame%2,0,0); return; }
        #endif
        float time = float(shift-fs.x+d.x)/timescale/fsf.x;
        panImage(f, time, df.y);
    } else {
        f = texelFetch(iChannel0,d+ivec2(delta,0),0);
    }
}

/*
Sound Experiment
Copyright 2018 Theron Tarigo
*/

const float pi = 3.141592653589793;

float sincstep(float x) {
    return ( x<0.001 ? 0. : 1.0-sin(pi*x)/(pi*x) );
}

float pulse1(float freq, float decay, float time) {
  return sincstep(time*freq*2.)*exp(-time*decay)*cos(time*freq*2.*pi);
}

float synth2(float freq, float time, float maxtime) {
  float v = 0.;
  for (float n=1.0; n<18.; n+=1.0) {
    v += pulse1(freq*n, pow(n,1.4)*0.2/pow(freq/180.,2.), time)*exp(-n*0.4);
  }
  if (time < 0. || time > maxtime) return 0.;
  v*=1.-exp((time/maxtime-1.)*8.);
  return v;
}

float selfproduct(vec3 v) { return v.x*v.y*v.z; }

ivec3[] seqN = ivec3[](
    ivec3(-4,0, 1),
    ivec3(-1,-1,0),
    ivec3( 0,-1,0),
    ivec3( 1,-2,0),
    ivec3(-1,-1,0),
    ivec3(-2,0, 0),
    ivec3(-3,1, 0),
    ivec3(-1,-1,0),
ivec3(0));

float seqf(int n, int s) {
  int i = n%8;
  int j = (n/(1))%8;
  int k = s;
  i ^= (s ^ (s>>1));
  ivec3 seq = seqN[i%8];
  return selfproduct(pow(vec3(2,3,5), vec3(seq)));
}

void seq0(float time, out float freq, int s) {
    int n = int(time);
    float fprod = seqf(n, s);
    freq = 180. * 1. * fprod;
}

vec2 smoothclip(vec2 f) {
    float p = 3.;
    f.x = pow(atan(pow(abs(f.x),p)*1.571)/1.571,1./p)*sign(f.x);
    f.y = pow(atan(pow(abs(f.y),p)*1.571)/1.571,1./p)*sign(f.y);
    return f;
}

vec2 mainSound( in int samp, float timein ) {
  float time = timein + 180.*0.;
  float br;
  vec2 v = vec2(0.);
  for (int i = 0; i < 5; i++) {
    float ampl;
    float freq;
    br = float(4<<i);
    float nr = 1./4.;
    float syntime = mod(time,1./br);
    seq0(time*br, freq, int(time*nr));
    freq *= pow(2., 3.-float(i));
    ampl = 0.25;
    ampl *= pow(.7, float(i));
    float pos = pi*sin(0.25/4.*pi*float((1<<i)*int(time*br)));
    float td = 0.0001;
    v.x += ampl*synth2(freq, syntime+td*sin(pos)-td, 1./br)*pow(cos(pos/2.-pi/4.),2.);
    v.y += ampl*synth2(freq, syntime-td*sin(pos)-td, 1./br)*pow(cos(pos/2.+pi/4.),2.);
  }
  v *= max(1.-exp(-10.*(178.-timein)),0.);
  v *= max(1.-1.1*exp(-0.2*timein),0.);
  v = smoothclip(v);
  return vec2(v);
}
