void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float time = iTime;
	vec2 adjp = fragCoord.xy / iResolution.xy - 0.5;
	float eye_offset = sign(adjp.x) * 0.6;
	if(adjp.x < 0.0) {
		adjp.x = (0.25 + adjp.x) * 2.0;
	} else {
		adjp.x = (adjp.x - 0.25) * 2.0;
	}
	vec2 p = adjp * abs(sin(time/10.0)) * 25.0;
	float 
		d = sin(length(p)+time), 
		a = sin(mod(atan(p.y, p.x) + time + sin(d+time), 3.1416/3.)*3./eye_offset), 
		v = a + d, 
		m = sin(length(p)*4.0-a+time);
	fragColor = vec4(
		-v*sin(m*sin(-d)+time*.1), 
		v*m*sin(tan(sin(-a))*sin(-a*3.)*3.+time*.5), 
		mod(v,m), 
		time);
}
