vec4 thing(vec3 rd){
    vec4 result = vec4(0.0);
    for(int n = 0; n < 64; n++){
        vec3 v = normalize(2.0 * texture(iChannel0, vec2(.125, float(n) / 128.0)).xyz - 1.0);
        result.xyz += 1.5/dot(rd, v) * texture(iChannel0, vec2(.25, float(n) / 128.0)).xyz;
    }
    return result;
}

vec3 r(vec3 v, vec2 r){//rodolphito's rotation
    vec4 t = sin(vec4(r, r + 1.5707963268));
    float g = dot(v.yz, t.yw);
    return vec3(v.x * t.z - g * t.x,
                v.y * t.w - v.z * t.y,
                v.x * t.x + g * t.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 m = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;
    vec3 rd = normalize(vec3(xy, 1.0));
    rd = r(rd, m);
    fragColor = thing(rd);
}
