vec4 translate(vec4 size, vec2 offset) {
    size.xz += offset.x;
    size.yw += offset.y;

	return size;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord - .5 * iResolution.xy ) / iResolution.y;

   	vec3 color = vec3(.0, .0, .0);

    vec4 rect = vec4(.0, .0, .15, .15);

    //	Translate
    //rect = translate(rect, vec2(.2, .2));
    float slowerRate = 4.;
    float rotate = 6.28319;
    float rotateTime = mod(iTime, slowerRate);
    rotate = rotate / slowerRate * rotateTime;
    float c = cos(rotate);
    float s = sin(rotate);
    mat2 rot = mat2(c, -s, s, c);

    pos *= rot;

    vec2 rc = step( abs(pos), rect.zw);

    color = vec3(rc.x * rc.y);



    fragColor = vec4(color, 1.0);
}
