#define PI 3.14159265359

void gradient( out vec4 fragColor, in vec2 fragCoord );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    gradient( fragColor, fragCoord );
}

void gradient( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 center = vec2(.5,.5);
    float x = (uv.x-center.x)*5.2*cos(iTime);
    float y = (uv.y-center.y)*5.2*sin(iTime);

    float l = (cos(x) + sin(y));

    float r = smoothstep(0.,1.1,l);
    float g = smoothstep(0.5,1.2,l);
    float b = smoothstep(0.7,1.3,l);

    vec4 color = vec4(r,g,b,1.0);
    fragColor = color;
}
