renderpass.code:
float noise(vec2 v) {
    return texture(iChannel1, v).r;	
}

float fbm(vec2 v) {
    float sum = 0.0;
    float n = 24.0;
	float t = iTime;

	v.x += 0.2*sin(t*2.0) * sin(v.y);
	float height = iTime;
	v.y -= height;

    sum += noise(v * 0.5)      * 4.01;
    sum += noise(v * 0.25)     * 4.02;
    sum += noise(v * 0.125)    * 4.03;
	sum += noise(v * 0.0625)   * 4.04;
	sum += noise(v * 0.03125)  * 4.05;
	sum += noise(v * 0.015625) * 4.06;	

    return sum / n;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((2.0 * fragCoord.xy) - iResolution.xy) / min(iResolution.x, iResolution.y);

	vec4 mixed;	
	float epsilon = 0.4434;
	float t = (2.0 + sin(iTime))/16.0;
	float fbm_noise = fbm(uv)/.4+t;

	mixed = mix(
		vec4(1.0, 0.1, 0.1, 1.0),
		vec4(0.0),
		fbm_noise + uv.y / epsilon
	);

	fragColor = mixed;
}
