float tau = atan(1.)*8.;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float time = iTime;
    vec2 p = fragCoord.xy / iResolution.xy * 2.0 - 1.0 ;
    p.x = p.x * 1.6;
    if(abs(p.x) < 1.0) {
		vec4 col;
		float x,y;
		x = atan(p.x,p.y);
		y = 1./length(p.xy);
		col.x = sin(x*5. + sin(time)/3.) * sin(y*5. + time);
		col.y = sin(x*5. - time + sin(y+time*3.));
		col.z = -col.x + col.y * sin(y*4.+time);
		col = clamp(col,0.,1.);
		col.y = pow(col.y,.5);
		col.z = pow(col.z,.1);
		fragColor = 1.-col*length(p.xy);
    } else {
        fragColor = vec4(0.0);
    }
}
