#define PI 3.14159265359
#define SLOWDOWN 3.
#define STARTTIME 1.5
#define SHOCKTIME PI/SLOWDOWN
#define SOUNDSPEED 1.

float dist(vec2 uv, float x, float y) {
	return sqrt(pow(abs(uv.x-x)*16./9.,2.)+ pow(abs(uv.y-y),2.));
}

vec2 deltaCoor(vec2 uv, vec2 center) {
	return vec2(uv.x-center.x, uv.y-center.y);
}

float shockwave(float rawX) {
	float x = rawX - STARTTIME/SLOWDOWN;
	float shock = sin(x*50.-PI)/(x*50.-PI);
	float squareFunction = ceil(x/1e10)-ceil((x-SHOCKTIME)/1e10);
	return shock*squareFunction;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	float noiseX = (texture(iChannel1, vec2(uv.x, uv.y)).r-.5)/50.;
	float noiseY = (texture(iChannel1, vec2(uv.y, uv.x)).r-.5)/50.;
	float d = dist(uv, .5+noiseX,.5+noiseY);

	float amp = shockwave(iTime/SLOWDOWN-d*SOUNDSPEED);
	float x = amp*(uv.x-.5);
	float y = amp*(uv.y-.5);

	vec4 image = texture(iChannel0, vec2(uv.x+x, 1.-uv.y+y));

	fragColor = vec4(image.r, image.g, image.b,1.0);
}
