float PIXEL_FACTOR = 6.0;

float smoothF(float f) {
	return 32.0*f*f*(0.25*f*f-0.5*f+0.25)+0.5;	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//PIXEL_FACTOR = 8.0 + 8.0 * (0.5 + 0.5 * sin(iTime * 0.25));
	vec2 chunkCoord = floor(fragCoord.xy / PIXEL_FACTOR) * PIXEL_FACTOR;
	vec2 locCoord = (fragCoord.xy - chunkCoord) / PIXEL_FACTOR;
	vec4 color = vec4(floor(5.0 * texture(iChannel0, chunkCoord / iResolution.xy).xyz) / 5.0, 1.0);
	float grey = (color.x + color.y + color.z) / 3.0;
	fragColor = color * smoothF(locCoord.x) * smoothF(locCoord.y);
}
