void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 med = vec2(0.5,0.5);//(uv*0.5) - 1.0;
	vec2 disVec = med-uv;
	float l = length(disVec);
	float ll = 1.0 - l*l;
	vec2 dist = med - disVec*ll;

	fragColor = texture(iChannel0, dist);



}
