void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    if (uv.x > 0.5) uv.x = 1.0-uv.x;
    if (uv.y > 0.5) uv.y = 1.0-uv.y;
    //uv.y+=fract(uv.y*10.+uv.x*10.)*0.01;
    //uv.x+=fract(uv.y*10.+uv.x*10.)*0.01;
	float t = uv.y*0.5+iTime*0.5+uv.y*1.1+uv.x*2.;
    uv+=fract(t+uv.x*2.)*0.1;
	vec3 c;
    float ss;
    for (float i = 0.0; i < 10.; i+=0.8) {
        vec3 co = vec3(1.0);
        ss = abs(0.3-0.2*(-fract(uv.x+t+i*t*0.01)*cos(uv.y+t+i*t*0.01))*i+i*0.1);
        vec2 ce = vec2(0.5);
        c += vec3(0.0+i*0.1*uv.y*1.2);

        c+=abs(cos(uv.x-t)+abs(sin(uv.y-t)*i*uv.y*0.1));

        if (uv.x < ce.x-ss || uv.x > ce.x+ss) c += co;
        if (uv.y < ce.y-ss || uv.y > ce.y+ss) c += co;

        c/=8.0-ss;

    }
	c/=1.0;

    c = clamp(c,0.0,1.0);

	fragColor = vec4(vec3(c.r*2.0+ss,c.g*3.0,c.b*4.0),1.0);
}
