void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 z = 1./iResolution.xy * (sin(uv.x + iTime))* 125.;

    //vec4 buffer = texture(iChannel1,vec2(uv.x,	uv.y);

    	z = z * mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime));


        vec4 tl  = texture(iChannel0,vec2(uv.x-z.x,	uv.y-z.y))	*-1.;
        vec4 t   = texture(iChannel0,vec2(uv.x-z.x,	uv.y))		*-1.;
        vec4 tr  = texture(iChannel0,vec2(uv.x-z.x,	uv.y+z.y))	*-1.;
        vec4 l   = texture(iChannel0,vec2(uv.x,		uv.y-z.y))	*-1.;
        vec4 c   = texture(iChannel0,vec2(uv.x,		uv.y))		*7.2;
        vec4 r   = texture(iChannel0,vec2(uv.x,		uv.y+z.y))	*-1.;
        vec4 dl  = texture(iChannel0,vec2(uv.x+z.x,	uv.y-z.y))	*-1.;
        vec4 d   = texture(iChannel0,vec2(uv.x+z.x,	uv.y))		*-1.;
        vec4 dr  = texture(iChannel0,vec2(uv.x+z.x,	uv.y+z.y))	*-1.;


    vec4 clr = (l+t+tr+l+c+r+dl+d+dr);

    fragColor = clr;
}
