#define PI 3.14159265359
#define PI_INV 0.31830988618
#define TAU 6.28318530718
#define DEG2RAD 0.01745329251
#define RAD2DEG 57.2957795131
#define SEED 935.45111
#define OCTAVES 7

float random (vec2 st) {
    return fract(SEED + sin(dot(st.xy,vec2(12.9898,78.233)))* 43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

//The Book of shaders
float fbm (in vec2 st) {
    // Initial values
    float value = 0.0;
    float amplitude = .5;
    float frequency = 0.;
    //
    // Loop of octaves
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * noise(st);
        st *= 2.;
        amplitude *= .5;
    }
    return value;
}

mat2 scale(float sx,float sy) {
  return mat2(sx,.0,.0,sy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   float time = iTime;
   vec2 uv = fragCoord.xy / iResolution.xy;
   vec2 center = uv+vec2(-0.5);


   vec2 uv1  = vec2(center);

   vec3 color = vec3(0.);

   uv1 = scale(2.2,0.8) * center + vec2(.0,.1);

  float t1 = fbm(uv1 + vec2(0.0,-time*2.1)) * 6.;
  float t2 = fbm(uv1 + vec2(0.0,-time*1.4) + vec2(34.3333,42.22111)) * 6.;

  float c1 = length(uv1*t1);
  float c2 = length(uv1*t2*4. + vec2(0.0,-.7));
  float c3 = length(uv1*2. + vec2(0.0,-.5));

  float finalC = c1 + c2 + c3;
  finalC = finalC/3.;

  float s = 0.999;
  float th = 0.08;

  float portal = 1. - smoothstep(finalC,s-th,s+th);

  float portalCenter = step(finalC,1.);

  float smoke = fbm(uv*0.8 + vec2(.0,-time*0.24));
  smoke = smoke * smoke * smoke;

  float otherWorld = fbm(uv*3.4 + vec2(.0,time*0.33));
  otherWorld =  otherWorld ;

  color = mix(vec3(0.42,0.,0.),vec3(1.,1.,0.),smoke);
  color = mix(color,vec3(1.,0.93,0.),portal);

  vec3 otherColor = mix(vec3(0.4,0.4,.3),vec3(0.,0.12,.24),otherWorld);

  color = mix(color,otherColor,portalCenter);

  fragColor = vec4(color, 1.);
}
