void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 nuv = uv * 2.0 - 1.0;
    vec2 nnuv = nuv * vec2(1.0, iResolution.y / iResolution.x);

    float t = iTime;
    float bpm = 1.0 / 60.0 * 142.0 * 2.0;
    float bpmw = pow(abs(sin(t * bpm * 0.5)), 2.5);

    float m0 = texture(iChannel0, vec2(0.0, 0.25)).x;

    m0 += texture(iChannel0, vec2(0.05, 0.25)).x;
    m0 += texture(iChannel0, vec2(0.12, 0.25)).x;
    m0 += texture(iChannel0, vec2(0.2, 0.25)).x;

    m0 /= 4.0;

    m0 = pow(max(0.0, m0 - 0.25) * 2.0, 3.5);

    vec2 cylv = nnuv + vec2(0.0, 0.2 - m0 * 0.1);
    float cyl = (cylv.x * cylv.x) / 0.25 - 0.2 * m0 + (cylv.y * cylv.y) / 0.020;
    float c0 = max(0.0, 1.0 - cyl);

    c0 *= pow(uv.y, 1.0 + 2.0 * -cylv.y) * 8.0;

    float c1 = step(-0.0125, cylv.y);

    c1 *= step(-0.4, -cylv.y);

    float c2 = step(-0.40, cylv.x);

    c2 *= step(-0.40, -cylv.x);

    vec2 c3va = nnuv + vec2(0.0, 0.2);
    vec2 c3vb = nnuv + vec2(0.0, -0.05);
    float c3a = (c3va.x * c3va.x) / 0.25 + (c3va.y * c3va.y) / 0.020;
    float c3b = (c3vb.x * c3vb.x) / 0.25 + (c3vb.y * c3vb.y) / 0.020;

    float c3 = max(0.0, max(0.0, (1.25 + 0.05 * bpmw - c3a * 1.0)) - max(0.0, (1.0 - c3a) * 4.0));

    //fragColor.x = 1.0 * c0;
    //fragColor.y = 1.0 * c1;
    //fragColor.z = 1.0 * c2;

    fragColor.xyz += vec3(0.15);
    fragColor.xyz += vec3(0.0, 0.2, 0.1 + m0 * 0.1) * c0 * (1.0 + m0 * 0.4);
    fragColor.xyz += vec3(0.0, 0.3 + 0.1 * bpmw, 0.2) * c3;

    fragColor.xyz += vec3(texture(iChannel0, vec2(cyl)).x) * c0 * (0.05 + bpmw * m0 * 0.5);

    fragColor.xyz += vec3(c3) * 0.35;

    fragColor.xyz += vec3(texture(iChannel0, vec2(abs(nuv.x), abs(nuv.y)))).x * 0.7 * (1.0 - abs(nuv.y)) * max(0.0, (1.0 - abs(nuv.x * 1.0)) * m0);

    /// start white and fade out to color
    // or blink white on beat
    fragColor.xyz += vec3(c3 * c3b) * pow(abs(sin(t * bpm * 0.5 - uv.y * 4.0)), 2.5) * 0.15;
}
