#define rand(x) fract(sin(x)*1e4)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy,v=uv*(1.-uv);
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 ro=vec3(0.),rd=normalize(vec3(uv,1.)),dh=vec3(0.,0.,1.);
    float c=0.;
    for(float f=0.;f<64.;++f){
        float fr=rand(f)*100.+iTime,rr=fract(fr),rb=floor(fr);
        vec4 rnd=rand(rb+vec4(0.,3.,5.,8.));
        float a=rnd.w*6.2831;
        float r=1.;//rnd.x+rnd.y;r=min(r,2.-r);
        vec3 p=vec3(cos(a)*r,sin(a)*r,0.),rop=ro-p;
        float z=mix(50.,-10.,rr);
        vec3 n=normalize(cross(dh,rd));
        float d=abs(dot(n,rop));
        vec3 n2=cross(rd,n);
        float t=dot(rop,n2)/dot(dh,n2);
        c+=.002/d/d*step(0.,t)*smoothstep(5.,0.,abs(t-z))*max(0.,1.-.03*t);
    }
    vec3 clr=mix(vec3(1.,.1,.2),vec3(1.,.4,.2),c)*c;
    clr=sqrt(clr)*pow(v.x*v.y*25.,.25);
	fragColor = vec4(clr,0.);
}
